package conditionStatements;

public class IfAndElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=50;
		int num2=40;
		if (num1==num2) 
		{
			System.out.println("the num1 is equalTo num2 : "+num1);
			
		}
		else {
			System.out.println("the num1 is not-equalTo num2 : "+num1);
		}
	}

}
