		// TODO Auto-generated method stub
		int num=123;          
		String s1="rocky";
		String s2="ram";
		float f=100.2f;
		boolean b=true;
		char ch='R';
		long l=99999999L;
		
		System.out.println( "the integer value is : "+num);
